// Helper function to get synthetic genes by category
export const getSyntheticGenesByCategory = (category: string): SyntheticGene[] => {
  return syntheticGenes.filter(gene => gene.category === category)
}

// Helper function to search synthetic genes
export const searchSyntheticGenes = (query: string): SyntheticGene[] => {
  const lowerQuery = query.toLowerCase()
  return syntheticGenes.filter(gene => 
    gene.name.toLowerCase().includes(lowerQuery) ||
    gene.description.toLowerCase().includes(lowerQuery) ||
    gene.tags.some(tag => tag.toLowerCase().includes(lowerQuery))
  )
} 